document.addEventListener("DOMContentLoaded",(function(){const e=new IntersectionObserver((t=>{t.forEach((t=>{if(t.isIntersecting){let n=t.target,o=n.getAttribute("data-animation");lottie.loadAnimation({container:n,renderer:"svg",loop:!0,autoplay:!0,path:o}),e.unobserve(n)}}))}));document.querySelectorAll(".animationContainer").forEach((t=>{e.observe(t)}))}));